echo "[{\"utiltype\":\"CPU\",\"utilkey\":\"CPU_UTIL\",\"uom\":\"KBs\",\"value\":`top -b -n1 | grep "Cpu(s)" | awk '{print $2 + $4}'`"\},"{\"utiltype\":\"CPU\",\"uom\":\"GHz\",\"utilkey\":\"CPU_SPEED\",\"value\":`lscpu |awk -F : '($1=="CPU MHz") {printf "%3.2f\n", $2/1000}'`"\},"{\"utiltype\":\"DISK\",\"utilkey\":\"DISK_READ\",\"uom\":\"KBs\",\"value\":`df -lh | awk '{if ($6 == "/") { print $5 }}' | head -1 | cut -d'%' -f1`"\},"{\"utiltype\":\"MEMORY\",\"utilkey\":\"MEM_FREE\",\"uom\":\"GB\",\"value\":`awk '/MemFree/ { printf "%.3f \n", $2/1024/1024 }' /proc/meminfo`"\},"{\"utiltype\":\"MEMORY\",\"utilkey\":\"MEM_TOTAL\",\"uom\":\"GB\",\"value\":`awk '/MemTotal/ { printf "%.0f \n", $2/1024/1024 }' /proc/meminfo`"\},"{\"utiltype\":\"MEMORY\",\"utilkey\":\"MEM_USEPERCENT\",\"uom\":\"PERCENT\",\"value\":`free | awk '/Mem/{printf  $3/$2*100}'`"\},"{\"utiltype\":\"ETHERNET\",\"utilkey\":\"NET_RECV\",\"value\":`ifstat -j`"\}]
